INSERT INTO articles (title, excerpt, content, category, tags, author_id, status, views, read_time, published_at) VALUES
('دليل المبتدئين لتعلم البرمجة من الصفر', 'خطوات عملية ونصائح مهمة للبدء في رحلة تعلم البرمجة بطريقة صحيحة وفعالة', 'البرمجة أصبحت من أهم المهارات في العصر الحديث، وتعلمها يفتح أبواب كثيرة للفرص المهنية والإبداعية. في هذا المقال، سنتناول الخطوات الأساسية لبدء رحلة تعلم البرمجة من الصفر...', 'تعليمي', '{"مبتدئين", "أساسيات", "نصائح"}', (SELECT id FROM users WHERE email = 'ahmed@example.com'), 'منشور', 2340, 5, NOW() - INTERVAL '2 days'),
('أفضل الممارسات في كتابة الكود النظيف', 'تعلم كيفية كتابة كود قابل للقراءة والصيانة باتباع أفضل الممارسات المعترف بها', 'الكود النظيف ليس مجرد كود يعمل، بل كود يمكن فهمه وصيانته بسهولة. في هذا المقال، سنستكشف أهم المبادئ والممارسات التي تساعد في كتابة كود عالي الجودة...', 'تطوير', '{"كود نظيف", "أفضل الممارسات", "جودة"}', (SELECT id FROM users WHERE email = 'fatima@example.com'), 'منشور', 1890, 8, NOW() - INTERVAL '4 days'),
('مقدمة في خوارزميات البحث والترتيب', 'شرح مبسط لأهم خوارزميات البحث والترتيب مع أمثلة عملية وتحليل الأداء', 'خوارزميات البحث والترتيب من الأساسيات المهمة في علوم الحاسوب. في هذا المقال، سنتعرف على أشهر هذه الخوارزميات وكيفية تطبيقها...', 'خوارزميات', '{"خوارزميات", "بحث", "ترتيب"}', (SELECT id FROM users WHERE email = 'mohammed@example.com'), 'منشور', 1560, 12, NOW() - INTERVAL '7 days');
INSERT INTO news (title, excerpt, content, category, author_id, status, views, featured, published_at) VALUES
('إطلاق مسابقة البرمجة الكبرى 2024', 'انضم إلى أكبر مسابقة برمجة في الوطن العربي واربح جوائز قيمة تصل إلى 50,000 جنيه', 'نعلن بفخر عن إطلاق مسابقة البرمجة الكبرى 2024، والتي تهدف إلى اكتشاف أفضل المواهب البرمجية في الوطن العربي. المسابقة مفتوحة لجميع الطلاب والمبرمجين الشباب...', 'مسابقات', (SELECT id FROM users WHERE email = 'admin@platform.com'), 'منشور', 1250, TRUE, NOW() - INTERVAL '1 day'),
('تحديث جديد: إضافة لغة Python للمسائل', 'يمكنك الآن حل جميع المسائل باستخدام لغة Python بالإضافة إلى اللغات المتاحة', 'استجابة لطلبات المستخدمين، قمنا بإضافة دعم كامل للغة Python في جميع المسائل البرمجية. يمكن الآن للطلاب استخدام Python لحل المسائل والاستفادة من مكتباتها القوية...', 'تحديثات', (SELECT id FROM users WHERE email = 'admin@platform.com'), 'منشور', 890, FALSE, NOW() - INTERVAL '3 days'),
('ورشة عمل: أساسيات خوارزميات البحث', 'ورشة عمل مجانية عبر الإنترنت لتعلم أساسيات خوارزميات البحث والترتيب', 'ندعوكم للمشاركة في ورشة العمل المجانية حول أساسيات خوارزميات البحث والترتيب. الورشة ستكون يوم السبت القادم الساعة 7 مساءً...', 'فعاليات', (SELECT id FROM users WHERE email = 'admin@platform.com'), 'منشور', 650, FALSE, NOW() - INTERVAL '5 days');
